import React, { useState, useEffect } from "react";
import { getPetsByUser, registerDevice, getMyDevices } from "../api/api";
import Navbar from "../components/Navbar";

function DeviceManagement() {
  const [pets, setPets] = useState([]);
  const [devices, setDevices] = useState([]);
  const [selectedPet, setSelectedPet] = useState("");
  const [deviceId, setDeviceId] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchPets();
    fetchDevices();
  }, []);

  const fetchPets = async () => {
    try {
      const res = await getPetsByUser();
      setPets(res.data.pets || []);
    } catch (error) {
      console.error("Error fetching pets:", error);
      alert("L·ªói khi t·∫£i danh s√°ch pets");
    }
  };

  const fetchDevices = async () => {
    try {
      const res = await getMyDevices();
      setDevices(res.data.devices || []);
    } catch (error) {
      console.error("Error fetching devices:", error);
      alert("L·ªói khi t·∫£i danh s√°ch devices");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    if (!deviceId || !selectedPet) {
      alert("Vui l√≤ng nh·∫≠p Device ID v√† ch·ªçn pet");
      return;
    }

    setLoading(true);
    try {
      await registerDevice(deviceId, selectedPet);
      alert("‚úÖ ƒêƒÉng k√Ω device th√†nh c√¥ng!");
      setDeviceId("");
      setSelectedPet("");
      fetchDevices();
    } catch (error) {
      alert(
        "‚ùå L·ªói ƒëƒÉng k√Ω device: " +
          (error.response?.data?.message || "L·ªói kh√¥ng x√°c ƒë·ªãnh")
      );
    } finally {
      setLoading(false);
    }
  };

  const generateDeviceId = () => {
    const newId =
      "ESP32_" + Math.random().toString(36).substr(2, 9).toUpperCase();
    setDeviceId(newId);
  };

  return (
    <>
      <Navbar />
      <div className="container">
        <h2>üì± Qu·∫£n l√Ω Devices</h2>

        {/* Form ƒëƒÉng k√Ω device */}
        <div className="card">
          <h3>‚ûï ƒêƒÉng k√Ω Device M·ªõi</h3>
          <form onSubmit={handleRegister}>
            <div style={{ marginBottom: "20px" }}>
              <label
                style={{
                  display: "block",
                  marginBottom: "8px",
                  fontWeight: "600",
                  color: "#4a5568",
                }}
              >
                Device ID:
              </label>
              <div
                style={{ display: "flex", gap: "10px", marginBottom: "5px" }}
              >
                <input
                  placeholder="Nh·∫≠p Device ID"
                  value={deviceId}
                  onChange={(e) => setDeviceId(e.target.value)}
                  style={{ flex: 1 }}
                  required
                />
                <button
                  type="button"
                  onClick={generateDeviceId}
                  style={{ marginBottom: 0 }}
                >
                  üé≤ T·∫°o ID
                </button>
              </div>
              <small style={{ color: "#718096" }}>
                Device ID t·ª´ ESP32 ho·∫∑c t·∫°o m·ªõi
              </small>
            </div>

            <div style={{ marginBottom: "20px" }}>
              <label
                style={{
                  display: "block",
                  marginBottom: "8px",
                  fontWeight: "600",
                  color: "#4a5568",
                }}
              >
                Ch·ªçn Pet:
              </label>
              <select
                value={selectedPet}
                onChange={(e) => setSelectedPet(e.target.value)}
                required
                style={{
                  width: "100%",
                  padding: "12px",
                  border: "2px solid #e2e8f0",
                  borderRadius: "8px",
                }}
              >
                <option value="">-- Ch·ªçn pet --</option>
                {pets.map((pet) => (
                  <option key={pet._id} value={pet._id}>
                    {pet.name} ({pet.species})
                  </option>
                ))}
              </select>
            </div>

            <button type="submit" disabled={loading}>
              {loading ? "ƒêang ƒëƒÉng k√Ω..." : "üìù ƒêƒÉng k√Ω Device"}
            </button>
          </form>
        </div>

        {/* Danh s√°ch devices */}
        <div className="card">
          <h3>üìã Devices ƒê√£ ƒêƒÉng K√Ω</h3>
          {devices.length === 0 ? (
            <p
              style={{ textAlign: "center", color: "#718096", padding: "20px" }}
            >
              Ch∆∞a c√≥ device n√†o ƒë∆∞·ª£c ƒëƒÉng k√Ω
            </p>
          ) : (
            <div className="devices-list">
              {devices.map((device) => (
                <div key={device._id} className="device-item">
                  <div className="device-info">
                    <strong>Device ID: {device.deviceId}</strong>
                    <div>
                      <span className="pet-badge">
                        Pet: {device.petId?.name || "Unknown"}
                      </span>
                      <span className="species-badge">
                        {device.petId?.species || "Unknown"}
                      </span>
                    </div>
                    <small>
                      C·∫≠p nh·∫≠t:{" "}
                      {new Date(device.lastSeen).toLocaleString("vi-VN")}
                    </small>
                  </div>
                  <div className="device-status">
                    <span
                      className={`status ${
                        device.isActive ? "active" : "inactive"
                      }`}
                    >
                      {device.isActive ? "üü¢ Active" : "üî¥ Inactive"}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* H∆∞·ªõng d·∫´n s·ª≠ d·ª•ng */}
        <div
          className="card"
          style={{ background: "#f0f9ff", borderLeft: "4px solid #4299e1" }}
        >
          <h3>üìñ H∆∞·ªõng D·∫´n S·ª≠ D·ª•ng</h3>
          <ol style={{ paddingLeft: "20px", marginBottom: "20px" }}>
            <li style={{ marginBottom: "10px" }}>
              <strong>T·∫°o Device ID</strong> - Nh·∫•n n√∫t "T·∫°o ID" ho·∫∑c nh·∫≠p ID t·ª´
              ESP32
            </li>
            <li style={{ marginBottom: "10px" }}>
              <strong>Ch·ªçn Pet</strong> - Ch·ªçn pet m√† device s·∫Ω theo d√µi
            </li>
            <li style={{ marginBottom: "10px" }}>
              <strong>ƒêƒÉng k√Ω</strong> - Nh·∫•n "ƒêƒÉng k√Ω Device"
            </li>
            <li>
              <strong>C·∫•u h√¨nh ESP32</strong> - D√πng Device ID trong code ESP32
            </li>
          </ol>
          <div
            style={{
              background: "#e2e8f0",
              padding: "15px",
              borderRadius: "8px",
            }}
          >
            <strong>Code ESP32 m·∫´u:</strong>
            <pre
              style={{
                background: "#2d3748",
                color: "#e2e8f0",
                padding: "10px",
                borderRadius: "6px",
                marginTop: "10px",
                overflowX: "auto",
              }}
            >
              String deviceId = "{deviceId || "ESP32_ABC123XYZ"}";
            </pre>
          </div>
        </div>
      </div>
    </>
  );
}

export default DeviceManagement;
