import React, { useEffect, useState } from "react";
import { getPetsByUser, getAllPetData, deletePet } from "../api/api";
import { Link } from "react-router-dom";
import Navbar from "../components/Navbar";
import RealTimeMap from "../components/RealTimeMap";
import DashboardStats from "../components/DashboardStats";
import AlertSystem from "../components/AlertSystem";

function Dashboard() {
  const [pets, setPets] = useState([]);
  const [selectedPet, setSelectedPet] = useState(null);
  const [petData, setPetData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [deleting, setDeleting] = useState(false);

  useEffect(() => {
    fetchPets();
  }, []);

  const fetchPets = async () => {
    try {
      const res = await getPetsByUser();
      const petsData = res.data.pets || [];
      setPets(petsData);

      if (petsData.length > 0) {
        setSelectedPet(petsData[0]);
        await fetchPetData(petsData[0]._id);
      }
    } catch (err) {
      console.error("Error loading pets:", err);
      alert("L·ªói khi t·∫£i danh s√°ch pets");
    } finally {
      setLoading(false);
    }
  };

  const fetchPetData = async (petId) => {
    try {
      const res = await getAllPetData(petId);
      const data = res.data.data || [];
      setPetData(data);

      // N·∫øu kh√¥ng c√≥ d·ªØ li·ªáu th·∫≠t, d√πng sample data
      if (data.length === 0) {
        const sampleData = [
          {
            latitude: 10.8231,
            longitude: 106.6297,
            activityType: "walking",
            batteryLevel: 85,
            speed: 1.2,
            timestamp: new Date().toISOString(),
          },
        ];
        setPetData(sampleData);
      }
    } catch (err) {
      console.error("Error fetching pet data:", err);
      // Fallback to sample data
      const sampleData = [
        {
          latitude: 10.8231,
          longitude: 106.6297,
          activityType: "walking",
          batteryLevel: 85,
          speed: 1.2,
          timestamp: new Date().toISOString(),
        },
      ];
      setPetData(sampleData);
    }
  };

  const handlePetSelect = async (pet) => {
    setSelectedPet(pet);
    await fetchPetData(pet._id);
  };

  const handleDeletePet = async (petId, petName) => {
    if (
      !window.confirm(
        `B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a pet "${petName}"? H√†nh ƒë·ªông n√†y kh√¥ng th·ªÉ ho√†n t√°c.`
      )
    ) {
      return;
    }

    setDeleting(true);
    try {
      await deletePet(petId);

      const updatedPets = pets.filter((pet) => pet._id !== petId);
      setPets(updatedPets);

      if (selectedPet && selectedPet._id === petId) {
        if (updatedPets.length > 0) {
          setSelectedPet(updatedPets[0]);
          await fetchPetData(updatedPets[0]._id);
        } else {
          setSelectedPet(null);
          setPetData([]);
        }
      }

      alert(`‚úÖ ƒê√£ x√≥a pet "${petName}" th√†nh c√¥ng!`);
    } catch (error) {
      console.error("Error deleting pet:", error);
      let errorMessage = "L·ªói kh√¥ng x√°c ƒë·ªãnh";

      if (error.response) {
        if (error.response.status === 404) {
          errorMessage = "Kh√¥ng t√¨m th·∫•y pet ƒë·ªÉ x√≥a.";
        } else if (error.response.status === 403) {
          errorMessage = "B·∫°n kh√¥ng c√≥ quy·ªÅn x√≥a pet n√†y.";
        } else {
          errorMessage =
            error.response.data?.message ||
            `L·ªói server: ${error.response.status}`;
        }
      } else if (error.request) {
        errorMessage = "Kh√¥ng th·ªÉ k·∫øt n·ªëi ƒë·∫øn server.";
      } else {
        errorMessage = error.message;
      }

      alert(`‚ùå L·ªói khi x√≥a pet: ${errorMessage}`);
    } finally {
      setDeleting(false);
    }
  };

  return (
    <>
      <Navbar />
      <div className="container">
        <div className="dashboard-header">
          <h2>üêæ Dashboard Theo D√µi Pet</h2>
          <Link to="/add-pet">
            <button style={{ marginBottom: 0 }}>+ Th√™m Pet M·ªõi</button>
          </Link>
        </div>

        {loading ? (
          <div className="loading">ƒêang t·∫£i d·ªØ li·ªáu...</div>
        ) : pets.length === 0 ? (
          <div className="no-pets">
            <p>Ch∆∞a c√≥ pet n√†o. Th√™m pet ƒë·∫ßu ti√™n c·ªßa b·∫°n!</p>
            <Link to="/add-pet">
              <button>Th√™m Pet ƒê·∫ßu Ti√™n</button>
            </Link>
          </div>
        ) : (
          <>
            {/* Select Pet */}
            <div className="pet-selector">
              <label>Ch·ªçn Pet ƒë·ªÉ theo d√µi:</label>
              <select
                value={selectedPet?._id || ""}
                onChange={(e) => {
                  const pet = pets.find((p) => p._id === e.target.value);
                  if (pet) handlePetSelect(pet);
                }}
              >
                {pets.map((pet) => (
                  <option key={pet._id} value={pet._id}>
                    {pet.name} - {pet.species}
                  </option>
                ))}
              </select>
            </div>

            {selectedPet && (
              <>
                {/* Stats Cards */}
                <DashboardStats petData={petData} selectedPet={selectedPet} />

                {/* Map and Alerts Grid */}
                <div className="grid-layout">
                  <div>
                    <h3 style={{ marginBottom: "15px", color: "#2d3748" }}>
                      üó∫Ô∏è B·∫£n ƒê·ªì Theo D√µi Th·ªùi Gian Th·ª±c
                    </h3>
                    <RealTimeMap petData={petData} selectedPet={selectedPet} />
                  </div>

                  <div>
                    <AlertSystem petData={petData} selectedPet={selectedPet} />
                  </div>
                </div>

                {/* Pet List */}
                <div className="pet-list-section">
                  <div className="section-header">
                    <h3>üìã Danh S√°ch Pets C·ªßa B·∫°n</h3>
                    <small style={{ color: "#718096" }}>
                      T·ªïng s·ªë: {pets.length} pet(s)
                    </small>
                  </div>
                  <div className="pets-grid">
                    {pets.map((pet) => (
                      <div
                        key={pet._id}
                        className={`pet-card ${
                          selectedPet?._id === pet._id ? "active" : ""
                        }`}
                      >
                        <div
                          className="pet-info"
                          onClick={() => handlePetSelect(pet)}
                          style={{ cursor: "pointer", flex: 1 }}
                        >
                          <h4>{pet.name}</h4>
                          <p>
                            {pet.species} ‚Ä¢ {pet.breed}
                          </p>
                          <p>{pet.age} tu·ªïi</p>
                          <div className="pet-status">
                            <span className="status-dot"></span>
                            <span>ƒêang ho·∫°t ƒë·ªông</span>
                          </div>
                        </div>
                        <div className="pet-actions">
                          <button
                            onClick={() => handleDeletePet(pet._id, pet.name)}
                            disabled={deleting}
                            className="delete-btn"
                            title="X√≥a pet"
                          >
                            {deleting ? "‚è≥" : "üóëÔ∏è"}
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </>
            )}
          </>
        )}
      </div>
    </>
  );
}

export default Dashboard;
